svg.script:


xmlns = "http://www.w3.org/2000/svg";
xlink = "http://www.w3.org/1999/xlink";

function $e(i){ return document.createElementNS(xmlns, i); }
function Class(){ return function(arguments){ this.init(arguments); } }
function setAttrs(obj, values){ for (i in values) { obj.setAttributeNS(null, i, values[i]); } }
function randColor(){ return "rgb(" + parseInt(Math.random() * 255) + "," + parseInt(Math.random() * 255) + "," + parseInt(Math.random() * 255) + ")"; }

var JSVG = new Class();

JSVG.prototype = {

	root: null,
	designArea:null,

	init: function(){

		JSVG.root = document.documentElement;
		this.buildMenu();
		this.buildDesignArea();
	}

}

JSVG.prototype.buildDesignArea = function(){

	JSVG.designArea = new this.Element('rect', {
		"height": "100%",
		"width": "85%",
		"x": "15%",
		"y": 0,
		"fill": "#eee",
		"id": "designArea"
	});
};

JSVG.prototype.buildMenu = function(){

		var self = this; this.toggle = false;

		this.bg = new this.Element('rect',{
			"height": "100%",
			"width": "14.8%",
			"x": 0,
			"y": 0,
			"fill": "#333",
			"id": "mainLeftMenu"
		});

		// Rectangle in the left menu
		this.RectBtn = new this.Element('rect',{
			"height": "5em",
			"width": "5em",
			"x": "2em",
			"y": "2em",
			"fill": "#ddd",
			"id": "rectBtn"
		});	

		// Select A rectangle
		this.RectBtn.addEventListener('click',function(evt){

			setAttrs(this,{
				"fill":"yellow",
				"stroke": "blue"
			});

			JSVG.designArea.addEventListener('click',function(evt){

				var newElement = new self.Element('rect', {
					"height": "5em",
					"width": "5em",
					"x": evt.clientX,
					"y": evt.clientY,
					"fill": "#ff0000",
					"id": this.toString()
				});

				// For each new element 
				newElement.addEventListener('click',function(evt){

					setAttrs(this, {
						"fill": "yellow",
						"stroke": "blue"
					});
				},false);

			},false);	

		},false);		


		// Circle in the left menu
		this.CircleBtn = new this.Element('circle', {
			"r": "2.5em",
			"cx": "11.5em",
			"cy": "4.5em",
			"fill": "#ddd",
			"fill-opacity": 0.85,
			"stroke": "#000",
			"stroke-opacity": 0.85,
			"id": this.toString()
		});	

		// Select A rectangle
		this.CircleBtn.addEventListener('click',function(evt){

			setAttrs(this,{
				"fill":"yellow",
				"stroke": "blue"
			});

			JSVG.designArea.addEventListener('click',function(evt){

				new self.Element('circle', {
					"r": "5em",
					"cx": evt.clientX,
					"cy": evt.clientY,
					"fill": "#ddd",
					"fill-opacity": 0.85,
					"stroke": "#000",
					"stroke-opacity": 0.85,
					"id": this.toString()
				});
			},false);	

		},false);	

		this.toggle = new this.Element('rect',{
			"height": "100%",
			"width": ".2%",
			"x": "14.8%",
			"y": 0,
			"fill": "#000",
			"id": "toggle"
		});

		this.toggle.addEventListener('click',function(){
			self.bg.setAttribute("x", self.toogle ? "0" : "-15%");
			self.toggle.setAttribute("x", self.toogle ? "14.8%" : "0");
			JSVG.designArea.setAttribute("x", self.toogle ? "15%" : "0");
			JSVG.designArea.setAttribute("width", self.toogle ? "85%" : "100%");
			self.toogle = !self.toogle;
		},false);

}

JSVG.prototype.Element = function(element, attrs){

    var el = $e(element);
    setAttrs(el, attrs);
    JSVG.root.appendChild(el);

	return el;
}




new JSVG()
